const initialState = {
    counter: 0
}

const reducer = (state = initialState, action) => {

    switch (action.type) {
        case 'INCREMENT':
            counter: state.counter + 1;
            break;
        case 0:
            counter: state.counter - 1;
            break;

        default:
            counter: 0;
    }

    return state
}

export default reducer
