const initialState = {
    counter: 0
}

const reducer = (state = initialState, action) => {

    switch (action.type) {
        case 'INCREMENT':
            return {
                counter: state.counter + 1,
            }
        case 'DECREMENT': {
            counter: state.counter - 1;
            break;
        }
        case 'ADD': {
            counter: state.counter + 2;
            break;
        }
        case 'SUB': {
            counter: state.counter - 2;
            break;
        }

        default:
            counter: 0;
    }

    return state
}

export default reducer
