const initialState = {
    counter: 0
}

const reducer = (state = initialState, action) => {

    switch (action.type) {
        case 'INCREMENT':
            return {
                counter: state.counter + 1,
            }
        case 'DECREMENT': return {
            ...state,
            counter: state.counter - 1
        }
        case 'ADD': return {
            counter: state.counter + 2
        }
        case 'SUB': return {
            counter: state.counter - 2
        }

        default:
            return state;
    }

    return state
}

export default reducer
